import React from 'react';
//import ReactDOM from 'react-dom';
import { Link, Switch } from 'react-router-dom';
import { BrowserRouter as Router, Route} from "react-router-dom";
import '../home.css';
import axios from 'axios';

class addEmployee extends React.Component {
	constructor(props) {
        super(props);

        this.state = {
            bName: "",
            pAbrv: "",
            username: this.props.username,
            showMe: false
		}

    this.handleSubmit = this.handleSubmit.bind(this)
    this.handleChange = this.handleChange.bind(this)
	}

    handleSubmit(event) {
        event.preventDefault()

        axios
            .post('http://localhost:8080/addBusiness', {
                    businessName: this.state.bName,
                    phoneAbrv: this.state.pAbrv
            }).then(response => {
            }).catch(err => console.log(err))

            setTimeout(window.location.replace("/user"), 100);
            //window.location.replace("/user")
}

handleChange(event) { this.setState({ [event.target.name] : event.target.value }); }

 operation() {
    if(this.state.showMe == false){
      this.setState({showMe: true})
    }
    if(this.state.showMe == true){
      this.setState({showMe: false})
    }
  }


	render () {
     
        return (            
            
       
                                <div className = "addEmployee">

		                            <label type="button" className="add" onClick={()=>this.operation()}>Add Employee</label>
         
                                    {this.state.showMe?
                                             <form id="form" onSubmit={this.handleSubmit} method="post" noValidate>
                                                <br />
                                                   <label for="ssn"><b>Enter Business Name: </b></label>
                                                   <input id="ssn" name="ssn" onChange={this.handleChange} value={this.state.bName} />

                                                   <label for="fname"><b>Enter Business Name Phone Abbreviation:</b></label>
                                                   <input id="fname" name="fname" onChange={this.handleChange} value={this.state.pAbrv}/>

                                                   <label for="lname"><b>Enter Business Name Phone Abbreviation:</b></label>
                                                   <input id="lname" name="lname" onChange={this.handleChange} value={this.state.pAbrv}/>

                                                   <label for="contact"><b>Enter Business Name Phone Abbreviation:</b></label>
                                                   <input id="contact" name="contact" onChange={this.handleChange} value={this.state.pAbrv}/>

                                                    <label for="address"><b>Enter Business Name Phone Abbreviation:</b></label>
                                                   <input id="address" name="address" onChange={this.handleChange} value={this.state.pAbrv}/>

                                                   <label for="pay"><b>Enter Business Name Phone Abbreviation:</b></label>
                                                   <input id="pay" name="pay" onChange={this.handleChange} value={this.state.pAbrv}/>

                                                   <label for="group"><b>Enter Business Name Phone Abbreviation:</b></label>
                                                   <input id="group" name="group" onChange={this.handleChange} value={this.state.pAbrv}/>

                                                  <button type="submit">Add Business</button>
                                            </form> 
                                    :null}
                                </div>
        );
    }
}

export default addEmployee;
