import React, {Component} from 'react';
import './login.css';
import { Link } from 'react-router-dom';
import axios from 'axios';
import quearystring from 'querystring';

class register extends Component {
     constructor() {
    super();
	this.state = {
      username: '',
	  password: '',
      email: '',
	  phoneNum: '',
	  redirectTo: null
    }
   
   this.handleSubmit = this.handleSubmit.bind(this)
   this.handleChange = this.handleChange.bind(this)

 
  }
  
  

  handleChange(event) {
        this.setState({
            [event.target.name]: event.target.value
        })
    }

  handleSubmit(event) {
        event.preventDefault()
        console.log('handleSubmit')

        axios
            .post('http://localhost:8080/signup', {
                username: this.state.username,
                password: this.state.password,
                email: this.state.email,
                phone: this.state.phoneNum
            })
            .then(response => {
                console.log('login response: ')
                console.log(response)
                if (response.status === 200) {
                    // update App.js state
                    this.props.updateUser({
                        loggedIn: true,
                        username: response.data.username
                    })
                    // update the state to redirect to home
                    this.setState({
                        redirectTo: '/'
                    })

                    window.location.replace("/user");  
                }
            }).catch(error => {
                console.log('login error: ')
                console.log(error);
                
            })
    
		/*event.preventDefault()
        console.log('handleSubmit')
        axios.post('http://localhost:8080/login', {
			username: this.state.username,
			password: this.state.passord
		})
      .then(res => console.log(res.data));
             
			/* axios.post('http://localhost:8080/login', user)
      .then(res => console.log(res.data));*/
                
            }
    

	render() {
			return <div class = "form-group"> 

                      <p style={{fontSize: 20}} > register </p>
					 
                     <form onSubmit={this.onSubmit}>
                          <div class="imgcontainer">
                         
                          </div>

                          <div class="container">
                            <label for="uname"><b>Username</b></label>
                            <input className="form-input" type="text" id="username" name="username" placeholder="Username" value={this.state.username} onChange={this.handleChange} />

  
                            <label for="email"><b>Email Address</b></label>
                            <input className="form-input"
                                    type="text"
                                    id="email"
                                    name="email"
                                    placeholder="Email Address"
                                    value={this.state.email}
                                    onChange={this.handleChange}
                                />

                            
                            <label for="phoneNum"><b>Phone Number</b></label>
                            <input className="form-input"
                                    type="text"
                                    id="phoneNum"
                                    name="phoneNum"
                                    placeholder="Phone Number"
                                    value={this.state.phoneNum}
                                    onChange={this.handleChange}
                                />
                       

                            <label for="psw"><b>Password</b></label>
                            <input className="form-input"
                                    placeholder="password"
                                    type="password"
                                    name="password"
                                    value={this.state.password}
                                    onChange={this.handleChange}
                                />

                            <button type="submit" onClick={this.handleSubmit}>Register</button>
                            <label>
                              <input type="checkbox" name="remember" /> Remember me
                            </label>
                          </div>
                          
                        

                          <div class="container" style={{backgroundColor: '#f1f1f1'}}>
                            <button type="button" class="cancelbtn">Cancel</button>
                           </div>
                        </form>          
			</div>
	}
}

export default register;
