import React from 'react';
//import ReactDOM from 'react-dom';
import { Link, Switch } from 'react-router-dom';
import { BrowserRouter as Router, Route} from "react-router-dom";
import '../home.css';
import axios from 'axios';

class business extends React.Component {
	constructor(props) {
        super(props);

        this.state = {
            bName: '',
            pAbrv: ''
		}

    //this.bName = this.bName.bind(this)
    //this.pAbrv = this.pAbrv.bind(this)
     this.handleChange = this.handleChange.bind(this)


	}

    handleChange() {
        this.setState({
           bName: this.businessName,
           pAbrv: this.phoneAbrv,
        })
    }

    handleSubmit(event) {
        event.preventDefault()

        axios
            .post('http://localhost:8080/addBusiness', {
                username: this.state.username,
                password: this.state.password
            })
            .then(response => {
                console.log('login response: ')
                console.log(response)
                if (response.status === 200) {
                   this.setState({
                        _id: response.data.user._id,
                        business: response.data.user.local.business,
                        object: response.data.user.local.object,
                        loggedIn: true
				   })
             
                    // update App.js state
                    {/*this.props.updateUser({*/}
                        //loggedIn: true;
                        //user: response.data.local;
                        
                    // })
                    // update the state to redirect to home
                    console.log(this.state)
                    window.location.replace("/user");  
                    //return <Redirect action="/user" />
                }
            }).catch(error => {
                console.log('login error: ')
                console.log(error);
                
            })
}

	render () {
     
        return (            
            
            <div className = "section">

		     <label type="button" className="add" >Add Business</label>
         

                         <form id="form" action="/addBusiness" method="post" noValidate>

                               <input id="name" name="name" value={this.businessName} />
                               <input id="abrv" name="abrv" value={this.phoneAbrv}/>

                              <button type="submit">Add Business</button>
                        
                  

                            </form> 
            </div>
        );
    }
}

export default business;
