import React from 'react';
//import ReactDOM from 'react-dom';
import { Link, Switch } from 'react-router-dom';
import { BrowserRouter as Router, Route} from "react-router-dom";
import '../home.css';
import axios from 'axios';

class business extends React.Component {
	constructor(props) {
        super(props);

	}

    handleChange(event) {
        this.setState({
            [event.target.name]: event.target.value
        })
    }

    handleSubmit(event) {
        event.preventDefault()
        console.log('handleSubmit')

        axios
            .post('http://localhost:8080/login', {
                username: this.state.username,
                password: this.state.password
            })
            .then(response => {
                console.log('login response: ')
                console.log(response)
                if (response.status === 200) {
                   this.setState({
                        _id: response.data.user._id,
                        business: response.data.user.local.business,
                        object: response.data.user.local.object,
                        loggedIn: true
				   })
             
                    // update App.js state
                    {/*this.props.updateUser({*/}
                        //loggedIn: true;
                        //user: response.data.local;
                        
                    // })
                    // update the state to redirect to home
                    console.log(this.state)
                    window.location.replace("/user");  
                    //return <Redirect action="/user" />
                }
            }).catch(error => {
                console.log('login error: ')
                console.log(error);
                
            })
    
		/*event.preventDefault()
        console.log('handleSubmit')
        axios.post('http://localhost:8080/login', {
			username: this.state.username,
			password: this.state.passord
		})
      .then(res => console.log(res.data));
             
			/* axios.post('http://localhost:8080/login', user)
      .then(res => console.log(res.data));*/
                
            }
    

	render () {

        function createBusiness() {
            var businessName = prompt("Please enter the name of the business: ");
            var phoneAbv = prompt("Please enter the abbreviation of the business name: ");
       
            
		}
     
        return (            
            
            <div className = "section">

		     <label type="button" className="add" onClick={createBusiness}>Add Business</label>

                          <form onSubmit={this.handleSubmit} noValidate>
                            <div class="container">
                              <label for="uname"><b>Username</b></label>
                              <input className="form-input"
                                      type="text"
                                      id="businessName"
                                      name="businessName"
                                      placeholder="Username"
                                      value={this.businessName}
                                      onChange={this.handleChange}
                                  />

                              <label for="psw"><b>Password</b></label>
                              <input className="form-input"
                                      placeholder="password"
                                      type="password"
                                      name="password"
                                      value={this.phoneAbv}
                                      onChange={this.handleChange}
                                  />

                              <button type="submit">Login</button>
                        
                              <label>
                                <input type="checkbox" name="remember" /> Remember me
                              </label>
                            </div>
                          	<Link to="/register" className="nav-link">Register</Link>

                            </form> 
            </div>
        );
    }
}

export default business;
