import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import './home.css';
import "bootstrap/dist/css/bootstrap.min.css";
import { BrowserRouter as Router, Route} from "react-router-dom";
import axios from 'axios'

import Navbar2 from "./loginNavbar.component";
import userpage from "./userpage.component";
import Navbar from "./loginNavbar.component";

export default class home extends Component {
	  constructor() {
    super()
    this.state = {
      loggedIn: false,
      username: null
    }

    this.getUser = this.getUser.bind(this)
    this.componentDidMount = this.componentDidMount.bind(this)
    this.updateUser = this.updateUser.bind(this)
  }

  componentDidMount() {
    this.getUser()
  }

  updateUser (userObject) {
	console.log('user updated',userObject.form);
    this.setState(userObject)
  }

  getUser() {
    axios.get('/user/').then(response => {
      console.log('Get user response: ')
      console.log(response.data)
      if (response.data.user) {
        console.log('Get User: There is a user saved in the server session: ')

        this.setState({
          loggedIn: true,
          username: response.data.user.username
        })
      } else {
        console.log('Get user: no user');
        this.setState({
          loggedIn: false,
          username: null
        })
      }
    })
  }

	render () {
		return (
			<Router> 
			
			<div className = "section">
			<p style={{fontSize: 50}} > Welcome to BusiTrack! </p>
			{/*<Navbar2 />*/}
	
			<br />
			 <Navbar updateUser={this.updateUser} loggedIn={this.state.loggedIn} />

          {/* greet user if logged in: */}
                {this.state.loggedIn &&
             <p>login success!, {this.state.username}!</p>
         }
        {/* Routes to different components */}

			<Route path="/user" exact component={userpage} />

			

			</div>
			</Router> 
		);
	}
}
