import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import './home.css';
import "bootstrap/dist/css/bootstrap.min.css";
import { BrowserRouter as Router, Route} from "react-router-dom";
import axios from 'axios';

import business from "./business.component";
import login from "./login.component";
import { buildQueries } from '@testing-library/react';

export default class Card extends Component {
	 constructor(props) {
        super(props);

      this.handleBusinessClick = this.handleBusinessClick.bind(this);
      this.view = this.view.bind(this)
      this.state = {
        viewBusiness: 'false'
      }
    }    

	 handleClick(e) {
		e.preventDefault();
		 console.log('The link was clicked.');
	return <business />
   
  }

    handleBusinessClick() {
      console.log('handle business fired')
    if(this.state.viewBusiness == 'false'){
      this.setState({viewBusiness: 'true'})
    }
    if(this.state.viewBusiness == 'true'){
      this.setState({viewBusiness: 'false'})
    }
    this.view();
  }

  view(){
    console.log('View function fired')
    console.log(this.state.viewBusiness)
    const view = this.state.viewBusiness;
    if(view == 'true'){
      return <business />
    }
  }

  
	render() {
    
    const viewBusiness = this.state.viewBusiness;
    const view = () => {
      console.log('test');
      if(viewBusiness == 'true'){
        return <business />;
      }
      else{
        return '';
      }
      
    }

		return (
				
			
			<div className = "section">
				<div className="businessCard_Container">
					<div className="businessCard">
						<div className="businessCardBanner">{this.props.businessName} </div>
						<div className="businessCardInfo">
						<div className="add">  
					    <button onClick={this.handleBusinessClick}>Business Page</button>
		      	</div>
						</div>
					</div>
				</div>
        {this.view}
        {this.state.viewBusiness}
			</div>
			
		);
	}
}
